const body = $input.first().json.body || {};
const message = (typeof body.message === 'string') ? body.message : '';
let sessionId = (typeof body.session_id === 'string') ? body.session_id : '';
if (!sessionId || sessionId.trim() === '') {
  sessionId = 'SESSION-' + Date.now() + '-' + Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 8);
} else if (/\.\.[\/\\]/.test(sessionId)) {
  throw new Error('Invalid session_id format');
}
let sanitizedMessage = message.replace(/<[^>]*>/g, '').trim();
if (sanitizedMessage.length > 1500) {
  sanitizedMessage = sanitizedMessage.substring(0, 1500);
}
return {
  json: {
    message: '[Data atual: ' + (() => {
  const now = new Date();
  const month = now.getUTCMonth();
  const isDST = month >= 2 && month <= 9;
  const offset = isDST ? 0 : -1;
  const local = new Date(now.getTime() + offset * 3600000);
  const days = ['domingo','segunda-feira','terça-feira','quarta-feira','quinta-feira','sexta-feira','sábado'];
  const months = ['janeiro','fevereiro','março','abril','maio','junho','julho','agosto','setembro','outubro','novembro','dezembro'];
  return days[local.getUTCDay()] + ', ' + local.getUTCDate() + ' de ' + months[local.getUTCMonth()] + ' de ' + local.getUTCFullYear() + ', ' + String(local.getUTCHours()).padStart(2,'0') + ':' + String(local.getUTCMinutes()).padStart(2,'0');
})() + '] ' + (sanitizedMessage || 'hello'),
    session_id: sessionId,
    headers: $input.first().json.headers || {},
    query: $input.first().json.query || {}
  }
};

const inputData = $input.first().json;
const flowiseResponse = inputData.text || inputData.output || '';
const isFallback = !flowiseResponse || flowiseResponse.trim() === '';

let sessionId = 'unknown';
let customerMessage = '';
try {
  const validateData = $('Validate Input').first().json;
  sessionId = validateData.session_id || 'unknown';
  customerMessage = validateData.message || '';
} catch (e) {}

let output = flowiseResponse;
if (isFallback) {
  output = '[INTENT:error] Peço desculpa, mas não consegui processar o seu pedido neste momento. Por favor, tente novamente ou entre em contacto connosco através de apoio@descomplicador.pt ou +351 926 874 141.';
}

// Extract [INTENT:xxx] tag
let llmIntent = null;
const intentMatch = output.match(/^\s*\[INTENT:(\w+)\]/);
if (intentMatch) {
  llmIntent = intentMatch[1].toLowerCase();
  output = output.replace(/^\s*\[INTENT:\w+\]\s*/, '');
}

// Check for LEAD_DATA
let leadData = null;
let hasLead = false;
const leadMatch = output.match(/\[LEAD_DATA\]\s*(\{[\s\S]*?\})\s*\[\/LEAD_DATA\]/);
if (leadMatch) {
  try {
    leadData = JSON.parse(leadMatch[1].trim());
    if (!leadData.customer_name || !leadData.customer_email || !leadData.business_type) {
      leadData = null;
    } else {
      // Validate email
      if (leadData.customer_email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(leadData.customer_email)) {
        leadData = null;
      } else {
        leadData.reference_id = 'LEAD-' + Date.now() + '-' + Math.random().toString(36).substring(2, 10).toUpperCase();
        leadData.session_id = sessionId;
        leadData.created_at = new Date().toISOString();
        for (const key of Object.keys(leadData)) {
          if (typeof leadData[key] === 'string') {
            leadData[key] = leadData[key].replace(/[\n\r]/g, ' ').substring(0, 500);
          }
        }
        hasLead = true;
      }
    }
  } catch (e) { leadData = null; }
}

let cleanResponse = output.replace(/\[LEAD_DATA\]\s*\{[\s\S]*?\}\s*\[\/LEAD_DATA\]/, '').trim();
if (hasLead && leadData) {
  cleanResponse = cleanResponse.replace(/LEAD-X+/g, leadData.reference_id);
}

let classifiedIntent = 'general';
if (hasLead) {
  classifiedIntent = 'lead_capture';
} else if (llmIntent) {
  classifiedIntent = llmIntent;
}

return [{
  json: {
    output: cleanResponse,
    hasLead,
    leadData,
    referenceId: hasLead ? leadData?.reference_id : null,
    sessionId,
    originalMessage: customerMessage,
    classifiedIntent,
    isFallback
  }
}];

let processData;
try { processData = $('Process Response').first().json; } catch (e) {
  processData = { output: 'Pedido processado. Entre em contacto através de apoio@descomplicador.pt.', sessionId: 'unknown', referenceId: null, classifiedIntent: 'lead_capture' };
}
const emailInput = $input.first().json;
const emailSuccess = emailInput && !emailInput.error;
return [{ json: {
  response: processData.output,
  intent: 'lead_capture',
  session_id: processData.sessionId,
  reference_id: processData.referenceId,
  lead_submitted: emailSuccess,
  email_error: emailSuccess ? undefined : (emailInput?.error?.message || 'Email delivery failed'),
  timestamp: new Date().toISOString()
}}];

let processData;
try { processData = $('Process Response').first().json; } catch (e) {
  processData = { output: 'Em que posso ajudar?', sessionId: 'unknown', classifiedIntent: 'general' };
}
return [{ json: {
  response: processData.output,
  intent: processData.classifiedIntent || 'general',
  session_id: processData.sessionId,
  timestamp: new Date().toISOString()
}}];
