const body = $input.first().json.body || {};
const message = (typeof body.message === 'string') ? body.message : '';
let sessionId = (typeof body.session_id === 'string') ? body.session_id : '';
if (!sessionId || sessionId.trim() === '') {
  sessionId = 'SESSION-' + Date.now() + '-' + Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 8);
} else if (/\.\.[\/\\]/.test(sessionId)) {
  throw new Error('Invalid session_id format');
}
let sanitizedMessage = message.replace(/<[^>]*>/g, '').trim();
if (sanitizedMessage.length > 1500) {
  sanitizedMessage = sanitizedMessage.substring(0, 1500);
}
return {
  json: {
    message: '[Data atual: ' + (() => {
  const now = new Date();
  const month = now.getUTCMonth();
  const isDST = month >= 2 && month <= 9;
  const offset = isDST ? 0 : -1;
  const local = new Date(now.getTime() + offset * 3600000);
  const days = ['domingo','segunda-feira','terça-feira','quarta-feira','quinta-feira','sexta-feira','sábado'];
  const months = ['janeiro','fevereiro','março','abril','maio','junho','julho','agosto','setembro','outubro','novembro','dezembro'];
  return days[local.getUTCDay()] + ', ' + local.getUTCDate() + ' de ' + months[local.getUTCMonth()] + ' de ' + local.getUTCFullYear() + ', ' + String(local.getUTCHours()).padStart(2,'0') + ':' + String(local.getUTCMinutes()).padStart(2,'0');
})() + '] ' + (sanitizedMessage || 'hello'),
    session_id: sessionId,
    headers: $input.first().json.headers || {},
    query: $input.first().json.query || {}
  }
};

const inputData = $input.first().json;
const flowiseResponse = inputData.text || inputData.output || '';
const isFallback = !flowiseResponse || flowiseResponse.trim() === '';

let sessionId = 'unknown';
let guestMessage = '';
try {
  const validateData = $('Validate Input').first().json;
  sessionId = validateData.session_id || 'unknown';
  guestMessage = validateData.message || '';
} catch (e) {}

let output = flowiseResponse;
if (isFallback) {
  output = '[INTENT:error] Peço desculpa, mas não consegui processar o seu pedido neste momento. Por favor, tente novamente ou contacte a receção em +XX XXX XXX XXX ou your-email@example.com.';
}

// Extract [INTENT:xxx] tag
let llmIntent = null;
const intentMatch = output.match(/^\s*\[INTENT:(\w+)\]/);
if (intentMatch) {
  llmIntent = intentMatch[1].toLowerCase();
  output = output.replace(/^\s*\[INTENT:\w+\]\s*/, '');
}

// Check for BOOKING_DATA
let bookingData = null;
let hasBooking = false;
const bookingMatch = output.match(/\[BOOKING_DATA\]\s*(\{[\s\S]*?\})\s*\[\/BOOKING_DATA\]/);
if (bookingMatch) {
  try {
    bookingData = JSON.parse(bookingMatch[1].trim());
    if (!bookingData.guest_name || !bookingData.booking_type || !bookingData.date || !bookingData.time) {
      bookingData = null;
    } else {
      bookingData.reference_id = 'BOOK-' + Date.now() + '-' + Math.random().toString(36).substring(2, 10).toUpperCase();
      bookingData.session_id = sessionId;
      bookingData.created_at = new Date().toISOString();
      for (const k of Object.keys(bookingData)) {
        if (typeof bookingData[k] === 'string') bookingData[k] = bookingData[k].replace(/[\n\r]/g, ' ').substring(0, 500);
      }
      hasBooking = true;
    }
  } catch (e) { bookingData = null; }
}

// Check for FEEDBACK_DATA
let feedbackData = null;
let hasFeedback = false;
const feedbackMatch = output.match(/\[FEEDBACK_DATA\]\s*(\{[\s\S]*?\})\s*\[\/FEEDBACK_DATA\]/);
if (feedbackMatch) {
  try {
    feedbackData = JSON.parse(feedbackMatch[1].trim());
    if (!feedbackData.guest_name || !feedbackData.feedback_type || !feedbackData.description) {
      feedbackData = null;
    } else {
      feedbackData.reference_id = 'FDBK-' + Date.now() + '-' + Math.random().toString(36).substring(2, 10).toUpperCase();
      feedbackData.session_id = sessionId;
      feedbackData.created_at = new Date().toISOString();
      for (const k of Object.keys(feedbackData)) {
        if (typeof feedbackData[k] === 'string') feedbackData[k] = feedbackData[k].replace(/[\n\r]/g, ' ').substring(0, 500);
      }
      hasFeedback = true;
    }
  } catch (e) { feedbackData = null; }
}

let cleanResponse = output
  .replace(/\[BOOKING_DATA\]\s*\{[\s\S]*?\}\s*\[\/BOOKING_DATA\]/, '')
  .replace(/\[FEEDBACK_DATA\]\s*\{[\s\S]*?\}\s*\[\/FEEDBACK_DATA\]/, '')
  .trim();

let classifiedIntent = 'general';
if (hasBooking) classifiedIntent = 'booking_request';
else if (hasFeedback) classifiedIntent = 'feedback';
else if (llmIntent) classifiedIntent = llmIntent;

return [{
  json: {
    output: cleanResponse,
    hasBooking,
    hasFeedback,
    bookingData,
    feedbackData,
    referenceId: hasBooking ? bookingData?.reference_id : (hasFeedback ? feedbackData?.reference_id : null),
    sessionId,
    originalMessage: guestMessage,
    classifiedIntent,
    isFallback
  }
}];

let d; try { d = $('Process Response').first().json; } catch(e) { d = { output: 'Pedido processado.', sessionId: 'unknown', referenceId: null }; }
const ei = $input.first().json; const ok = ei && !ei.error;
return [{ json: { response: d.output, intent: 'booking_request', session_id: d.sessionId, reference_id: d.referenceId, booking_submitted: ok, email_error: ok ? undefined : (ei?.error?.message || 'Failed'), timestamp: new Date().toISOString() }}];

let d; try { d = $('Process Response').first().json; } catch(e) { d = { output: 'Feedback registado.', sessionId: 'unknown', referenceId: null }; }
const ei = $input.first().json; const ok = ei && !ei.error;
return [{ json: { response: d.output, intent: 'feedback', session_id: d.sessionId, reference_id: d.referenceId, feedback_submitted: ok, email_error: ok ? undefined : (ei?.error?.message || 'Failed'), timestamp: new Date().toISOString() }}];
