// Auth check
const input = $input.item.json;
const headers = input.headers || {};
const vapiSecret = headers['x-vapi-secret'] || '';
const expected = process.env.VAPI_WEBHOOK_SECRET;
if (!expected) {
  throw new Error('VAPI_WEBHOOK_SECRET not configured');
}
if (vapiSecret !== expected) {
  return { _error: true, errorMessage: 'Pedido nao autorizado.', toolCallId: '' };
}

// Extract toolCallId first
let toolCallId = '';
try {
  toolCallId = input.body?.message?.toolCalls?.[0]?.id || '';
} catch (e) {}

try {
  // Extract body from Vapi format
  let body;
  if (input.body?.message?.toolCalls?.[0]?.function?.arguments) {
    body = input.body.message.toolCalls[0].function.arguments;
  } else if (input.body) {
    body = input.body;
  } else {
    body = input;
  }

  // Validate required fields
  if (!body.subject || body.subject.trim().length === 0) {
    return { _error: true, errorMessage: 'O assunto do email e obrigatorio. Qual o assunto da mensagem?', toolCallId };
  }
  if (!body.message || body.message.trim().length === 0) {
    return { _error: true, errorMessage: 'A mensagem do email e obrigatoria. O que gostaria de comunicar?', toolCallId };
  }
  if (body.message.length > 5000) {
    return { _error: true, errorMessage: 'A mensagem e demasiado longa. Por favor, resuma a mensagem.', toolCallId };
  }

  // Sanitize inputs
  function sanitize(text) {
    if (!text) return '';
    return String(text).replace(/<[^>]*>/g, '').substring(0, 1000);
  }

  // All emails go to owner - custom_recipient removed for security
  const recipientType = body.recipient_type || 'owner';

  return {
    _error: false,
    recipient_type: recipientType,
    subject: sanitize(body.subject.trim()),
    message: sanitize(body.message.trim()),
    customer_name: sanitize(body.customer_name || ''),
    customer_email: body.customer_email || '',
    customer_phone: body.customer_phone || '',
    toolCallId: toolCallId
  };
} catch (err) {
  return { _error: true, errorMessage: 'Ocorreu um erro ao preparar o email. Por favor, tente novamente.', toolCallId };
}

// All emails go to owner address
const defaultEmail = 'your-email@example.com';

// Build email body with context
const contactDetails = [];
if ($json.customer_name) contactDetails.push('Nome: ' + $json.customer_name);
if ($json.customer_email) contactDetails.push('Email: ' + $json.customer_email);
if ($json.customer_phone) contactDetails.push('Telemovel: ' + $json.customer_phone);

const emailBody = $json.message + (contactDetails.length > 0 ? '\n\n---\nDETALHES DO CONTACTO:\n' + contactDetails.join('\n') : '') + '\n\n---\nEnviado automaticamente via Assistente Virtual';

return {
  to_email: defaultEmail,
  subject: $json.subject,
  body: emailBody,
  recipient_type: $json.recipient_type,
  toolCallId: $json.toolCallId
};

const gmailResult = $input.item.json;
const prepData = $('Prepare Email').item.json;

// Check if Gmail send failed
if (gmailResult.error || (!gmailResult.id && !gmailResult.messageId && !gmailResult.threadId)) {
  return {
    message: 'Desculpe, nao foi possivel enviar o email neste momento. Pode tentar novamente mais tarde ou contactar-nos diretamente.',
    toolCallId: prepData.toolCallId
  };
}

return {
  message: 'Email enviado com sucesso. A nossa equipa ira responder em breve.',
  toolCallId: prepData.toolCallId
};
