parameters.jsCode:
// Auth check
const input = $input.item.json;
const headers = input.headers || {};
const vapiSecret = headers['x-vapi-secret'] || '';
const expected = process.env.VAPI_WEBHOOK_SECRET;
if (!expected) {
  throw new Error('VAPI_WEBHOOK_SECRET not configured');
}
if (vapiSecret !== expected) {
  return { _error: true, errorMessage: 'Pedido nao autorizado.', toolCallId: '' };
}

// Extract toolCallId first
let toolCallId = '';
try {
  toolCallId = input.body?.message?.toolCalls?.[0]?.id || '';
} catch (e) {}

try {
  // Extract body from Vapi format
  let body;
  if (input.body?.message?.toolCalls?.[0]?.function?.arguments) {
    body = input.body.message.toolCalls[0].function.arguments;
  } else if (input.body) {
    body = input.body;
  } else {
    body = input;
  }

  const reason = body.reason || 'Cliente solicitou atendimento humano';
  const department = (body.department || 'geral').toLowerCase();

  // Validate department
  const validDepartments = ['geral', 'general', 'sales', 'vendas', 'support', 'apoio', 'technical', 'tecnico', 'billing', 'faturacao'];
  const finalDepartment = validDepartments.includes(department) ? department : 'geral';

  // Map departments to phone numbers
  const defaultPhone = '+XX XXX XXX XXX';
  const departmentPhones = {
    'geral': defaultPhone, 'general': defaultPhone,
    'sales': defaultPhone, 'vendas': defaultPhone,
    'support': defaultPhone, 'apoio': defaultPhone,
    'technical': defaultPhone, 'tecnico': defaultPhone,
    'billing': defaultPhone, 'faturacao': defaultPhone
  };
  const transferNumber = departmentPhones[finalDepartment] || defaultPhone;

  // Map department names to pt-PT
  const departmentNames = {
    'geral': 'Atendimento Geral', 'general': 'Atendimento Geral',
    'sales': 'Vendas', 'vendas': 'Vendas',
    'support': 'Apoio', 'apoio': 'Apoio',
    'technical': 'Apoio Tecnico', 'tecnico': 'Apoio Tecnico',
    'billing': 'Faturacao', 'faturacao': 'Faturacao'
  };
  const departmentName = departmentNames[finalDepartment] || 'Atendimento Geral';

  return {
    _error: false,
    call_id: body.call_id || 'unknown',
    reason: reason,
    department: finalDepartment,
    department_name: departmentName,
    transfer_to: transferNumber,
    customer_name: body.customer_name || '',
    customer_phone: body.customer_phone || '',
    context: body.context || '',
    toolCallId: toolCallId
  };
} catch (err) {
  return { _error: true, errorMessage: 'Ocorreu um erro ao processar a transferencia. Por favor, tente novamente.', toolCallId };
}

