try {
// Dashboard Data Collector - Process & Aggregate
// Classifies datasets by _dataSource tag added by Set nodes
// Timezone: Atlantic/Azores with DST awareness

// --- Timezone: compute 'now' in Atlantic/Azores ---
const serverNow = new Date();
const utcMs = serverNow.getTime() + (serverNow.getTimezoneOffset() * 60000);
const month = new Date(utcMs).getUTCMonth();
// Azores DST: last Sunday of March to last Sunday of October
// Simplified: April(3) through October(9) = UTC+0, else UTC-1
const isDST = month >= 3 && month <= 9;
const offsetHours = isDST ? 0 : -1;
const now = new Date(utcMs + (offsetHours * 3600000));
let today = now.toISOString().split('T')[0];

// --- Optional date parameter from webhook ---
try {
  const webhookBody = $('Webhook - Manual Refresh').first().json.body;
  if (webhookBody && webhookBody.date && /^\d{4}-\d{2}-\d{2}$/.test(webhookBody.date)) {
    today = webhookBody.date;
  }
} catch (e) { /* scheduled run - no webhook body */ }

// --- Date Normalization ---
function normalizeDate(val) {
  if (val == null || val === '') return null;
  const s = String(val).trim();
  if (/^\d{4}-\d{2}-\d{2}$/.test(s)) return s;
  const dmy = s.match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})$/);
  if (dmy) return dmy[3] + '-' + dmy[2].padStart(2,'0') + '-' + dmy[1].padStart(2,'0');
  if (s.includes('T')) { const p = s.split('T')[0]; if (/^\d{4}-\d{2}-\d{2}$/.test(p)) return p; }
  const num = Number(s);
  if (!isNaN(num) && num > 10000 && num < 100000) {
    const epoch = new Date(Date.UTC(1899, 11, 30));
    epoch.setUTCDate(epoch.getUTCDate() + num);
    return epoch.toISOString().split('T')[0];
  }
  return null;
}

// --- Helpers ---
const safeInt = (v) => { const n = parseInt(v); return isNaN(n) ? 0 : n; };
const safeFloat = (v) => { const n = parseFloat(v); return isNaN(n) ? 0 : n; };
const capPct = (v) => Math.max(-999, Math.min(999, Math.round(v * 10) / 10));

function parseTime(dateStr, timeStr) {
  if (!timeStr || typeof timeStr !== 'string') return null;
  const match = timeStr.trim().match(/^(\d{1,2}):(\d{2})(?::\d{2})?$/);
  if (!match) return null;
  const d = new Date(dateStr + 'T' + match[1].padStart(2, '0') + ':' + match[2]);
  return isNaN(d.getTime()) ? null : d;
}

// --- Classify items by _dataSource tag ---
const allItems = $input.all();
const warnings = [];
let vendasData = [];
let tarefasData = [];
let stockData = [];
let equipaData = [];
let emailData = [];

for (const item of allItems) {
  const row = item.json;
  if (!row || Object.keys(row).length <= 1) continue;
  const src = row._dataSource;
  if (src === 'vendas') vendasData.push(row);
  else if (src === 'tarefas') tarefasData.push(row);
  else if (src === 'stock') stockData.push(row);
  else if (src === 'equipa') equipaData.push(row);
  else if (src === 'email') emailData.push(row);
}

// --- Track missing data sources ---
const sources = { Vendas: vendasData, Tarefas: tarefasData, Stock: stockData, Equipa: equipaData, Email: emailData };
for (const [name, data] of Object.entries(sources)) {
  if (data.length === 0) warnings.push(name + ': no data returned');
}
if (Object.values(sources).every(d => d.length === 0)) {
  throw new Error('All data sources failed - check spreadsheet ID, sheet names, and credentials');
}

// --- Process Sales ---
function processVendas(data) {
  const todayData = data.filter(r => normalizeDate(r.Date) === today);
  const yd = new Date(now); yd.setDate(yd.getDate() - 1);
  const yesterday = yd.toISOString().split('T')[0];
  const yesterdayData = data.filter(r => normalizeDate(r.Date) === yesterday);

  const receitaHoje = todayData.reduce((s, r) => s + safeFloat(r.Amount), 0);
  const receitaOntem = yesterdayData.reduce((s, r) => s + safeFloat(r.Amount), 0);
  const receitaMudanca = receitaOntem > 0 ? capPct((receitaHoje - receitaOntem) / receitaOntem * 100) : 0;

  const pedidosHoje = todayData.length;
  const pedidosOntem = yesterdayData.length;
  const pedidosMudanca = pedidosOntem > 0 ? capPct((pedidosHoje - pedidosOntem) / pedidosOntem * 100) : 0;

  const weekData = [];
  for (let i = 6; i >= 0; i--) {
    const d = new Date(now); d.setDate(d.getDate() - i);
    const ds = d.toISOString().split('T')[0];
    weekData.push(data.filter(r => normalizeDate(r.Date) === ds).reduce((s, r) => s + safeFloat(r.Amount), 0));
  }
  const totalSemana = weekData.reduce((s, v) => s + v, 0);
  const ticketMedio = pedidosHoje > 0 ? Math.round(receitaHoje / pedidosHoje * 100) / 100 : 0;

  const monthData = [];
  for (let w = 0; w < 4; w++) {
    let wt = 0;
    for (let d = 0; d < 7; d++) {
      const dt = new Date(now); dt.setDate(dt.getDate() - ((w * 7) + d));
      const ds = dt.toISOString().split('T')[0];
      wt += data.filter(r => normalizeDate(r.Date) === ds).reduce((s, r) => s + safeFloat(r.Amount), 0);
    }
    monthData.unshift(wt);
  }
  const totalMes = monthData.reduce((s, v) => s + v, 0);

  // Hourly: use real data if Hour column exists, otherwise flag as estimated
  const hasHour = todayData.length > 0 && todayData[0].Hour !== undefined;
  let todayChart;
  if (hasHour) {
    const hrs = ['09','10','11','12','13','14','15','16','17','18'];
    todayChart = {
      labels: hrs.map(h => h + 'h'),
      values: hrs.map(h => todayData.filter(r => String(r.Hour).padStart(2,'0') === h).reduce((s, r) => s + safeFloat(r.Amount), 0)),
      isEstimated: false
    };
  } else {
    const mult = [0.05,0.14,0.24,0.36,0.47,0.60,0.74,0.86,0.96,1];
    todayChart = {
      labels: ['9h','10h','11h','12h','13h','14h','15h','16h','17h','18h'],
      values: mult.map(f => Math.round(receitaHoje * f * 100) / 100),
      isEstimated: true
    };
  }

  return {
    receitaHoje, receitaMudanca, pedidosHoje, pedidosMudanca,
    ticketMedio, totalSemana, totalMes,
    today: todayChart,
    week: { labels: ['Seg','Ter','Qua','Qui','Sex','Sab','Dom'], values: weekData },
    month: { labels: ['Sem 1','Sem 2','Sem 3','Sem 4'], values: monthData }
  };
}

// --- Process Tasks ---
function processTarefas(data) {
  const s = (r) => (r.Status || '').toLowerCase();
  const abertas = data.filter(r => s(r) === 'open' || s(r) === 'pending').length;
  const concluidasHoje = data.filter(r => s(r) === 'completed' && normalizeDate(r.DueDate) === today).length;
  const atrasadas = data.filter(r => s(r) === 'overdue').length;
  return { abertas, concluidasHoje, atrasadas };
}

// --- Process Stock ---
function processStock(data) {
  const baixoStock = data.filter(r => {
    const qty = safeInt(r.Quantity);
    const min = safeInt(r.MinQuantity);
    return min > 0 && qty < min;
  }).map(r => ({
    produto: r.Product || 'Unknown',
    quantidade: safeInt(r.Quantity),
    minimo: safeInt(r.MinQuantity)
  }));
  const totalProdutos = data.length;
  const valorTotal = Math.round(data.reduce((s, r) => s + (safeInt(r.Quantity) * safeFloat(r.UnitPrice)), 0) * 100) / 100;
  return { baixoStock, totalProdutos, valorTotal };
}

// --- Process Team ---
function processEquipa(data) {
  const todayData = data.filter(r => normalizeDate(r.Date) === today && (r.Status || '').toLowerCase() === 'present');
  const presentes = todayData.length;

  const horasTrabalhadas = todayData.reduce((sum, r) => {
    const ci = parseTime(today, r.CheckIn);
    if (!ci) return sum;
    const co = parseTime(today, r.CheckOut) || now;
    let h = (co - ci) / 3600000;
    if (h < 0) h += 24;
    return sum + h;
  }, 0);

  const membros = todayData.map(r => {
    const ci = parseTime(today, r.CheckIn);
    let horas = 0;
    let emTurno = false;
    if (ci) {
      const co = parseTime(today, r.CheckOut);
      emTurno = !co;
      let h = ((co || now) - ci) / 3600000;
      if (h < 0) h += 24;
      horas = Math.round(h * 10) / 10;
    }
    return { nome: r.Name || 'Unknown', funcao: r.Role || '', horas, emTurno };
  });

  return { presentes, horasTrabalhadas: Math.round(horasTrabalhadas * 10) / 10, membros };
}

// --- Process Email ---
function processEmail(data) {
  const todayRow = data.find(r => normalizeDate(r.Date) === today);
  if (!todayRow && data.length > 0) warnings.push('Email: no data for today');
  return {
    naoLidas: safeInt(todayRow?.Unread),
    importantes: safeInt(todayRow?.Important),
    enviadasHoje: safeInt(todayRow?.Sent),
    taxaResposta: safeInt(todayRow?.ResponseRate)
  };
}

// --- Build output ---
const vendas = processVendas(vendasData);
const tarefas = processTarefas(tarefasData);
const stock = processStock(stockData);
const equipa = processEquipa(equipaData);
const email = processEmail(emailData);

return { json: {
  metadata: {
    generatedAt: now.toISOString(),
    timezone: 'Atlantic/Azores',
    warnings: warnings.length > 0 ? warnings : null,
    selectedDate: today,
    debug: { vendasRows: vendasData.length, sampleDate: vendasData.length > 0 ? vendasData[0].Date : null }
  },
  resumo: {
    receitaHoje: vendas.receitaHoje,
    receitaMudanca: vendas.receitaMudanca,
    pedidosHoje: vendas.pedidosHoje,
    pedidosMudanca: vendas.pedidosMudanca,
    tarefasAbertas: tarefas.abertas,
    tarefasConcluidas: tarefas.concluidasHoje,
    emailsNaoLidos: email.naoLidas,
    emailsImportantes: email.importantes
  },
  vendas, tarefas, stock, email, equipa
}};
} catch (err) {
  return { json: { error: true, message: err.message, metadata: { generatedAt: new Date().toISOString() } } };
}

