const input = $input.first().json;
const { startDate, endDate } = input;

// Validation
if (!startDate || !endDate) {
  return {
    json: {
      error: true,
      message: 'startDate and endDate are required'
    }
  };
}

const start = new Date(startDate);
const end = new Date(endDate);

// Check if dates are valid
if (isNaN(start.getTime()) || isNaN(end.getTime())) {
  return {
    json: {
      error: true,
      message: 'Invalid date format'
    }
  };
}

// Check startDate <= endDate
if (start > end) {
  return {
    json: {
      error: true,
      message: 'startDate must be before or equal to endDate'
    }
  };
}

return {
  json: {
    error: false,
    startDate,
    endDate,
    formattedStart: start.toLocaleDateString('pt-PT'),
    formattedEnd: end.toLocaleDateString('pt-PT')
  }
};

// HTML escape helper
function escapeHtml(text) {
  if (!text) return '';
  return String(text).replace(/[&<>"']/g, m => ({
    '&': '&amp;', '<': '&lt;', '>': '&gt;', '"': '&quot;', "'": '&#039;'
  }[m]));
}

// Generate HTML report
const { startDate, endDate, formattedStart, formattedEnd } = $input.first().json;
const salesData = $input.all();

// Calculate metrics
const totalRevenue = salesData.reduce((sum, row) => sum + (parseFloat(row.json.Amount) || 0), 0);
const totalOrders = salesData.length;
const avgTicket = totalOrders > 0 ? totalRevenue / totalOrders : 0;

const html = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Relatório de Negócios</title>
  <style>
    body { font-family: Arial, sans-serif; margin: 40px; color: #333; }
    .header { border-bottom: 3px solid #2563eb; padding-bottom: 20px; margin-bottom: 30px; }
    h1 { color: #2563eb; margin: 0; }
    .period { color: #666; font-size: 14px; margin-top: 10px; }
    .metrics { display: grid; grid-template-columns: repeat(3, 1fr); gap: 20px; margin: 30px 0; }
    .metric-card { background: #f3f4f6; padding: 20px; border-radius: 8px; }
    .metric-label { font-size: 12px; color: #666; text-transform: uppercase; }
    .metric-value { font-size: 28px; font-weight: bold; color: #111; margin-top: 10px; }
    .section { margin: 40px 0; }
    .section-title { font-size: 18px; font-weight: bold; margin-bottom: 15px; border-left: 4px solid #2563eb; padding-left: 10px; }
    table { width: 100%; border-collapse: collapse; }
    th, td { text-align: left; padding: 12px; border-bottom: 1px solid #e5e7eb; }
    th { background: #f9fafb; font-weight: 600; color: #374151; }
    .footer { margin-top: 50px; padding-top: 20px; border-top: 1px solid #e5e7eb; text-align: center; color: #666; font-size: 12px; }
  </style>
</head>
<body>
  <div class="header">
    <h1>Relatório de Negócios</h1>
    <div class="period">Período: ${escapeHtml(formattedStart)} - ${escapeHtml(formattedEnd)}</div>
    <div class="period">Gerado em: ${escapeHtml(new Date().toLocaleString('pt-PT'))}</div>
  </div>

  <div class="metrics">
    <div class="metric-card">
      <div class="metric-label">Receita Total</div>
      <div class="metric-value">€${totalRevenue.toFixed(2)}</div>
    </div>
    <div class="metric-card">
      <div class="metric-label">Total de Pedidos</div>
      <div class="metric-value">${totalOrders}</div>
    </div>
    <div class="metric-card">
      <div class="metric-label">Ticket Médio</div>
      <div class="metric-value">€${avgTicket.toFixed(2)}</div>
    </div>
  </div>

  <div class="section">
    <div class="section-title">Vendas Detalhadas</div>
    <table>
      <thead>
        <tr>
          <th>Data</th>
          <th>ID Pedido</th>
          <th>Cliente</th>
          <th>Valor</th>
          <th>Estado</th>
        </tr>
      </thead>
      <tbody>
        ${salesData.slice(0, 50).map(row => `
          <tr>
            <td>${escapeHtml(row.json.Date) || '-'}</td>
            <td>${escapeHtml(row.json.OrderID) || '-'}</td>
            <td>${escapeHtml(row.json.Customer) || '-'}</td>
            <td>€${parseFloat(row.json.Amount || 0).toFixed(2)}</td>
            <td>${escapeHtml(row.json.Status) || '-'}</td>
          </tr>
        `).join('')}
      </tbody>
    </table>
  </div>

  <div class="footer">
    <p>Dashboard de Negócios &copy; 2026 | Relatório gerado automaticamente</p>
  </div>
</body>
</html>
`;

return { json: { html, fileName: `relatorio-${escapeHtml(startDate)}-${escapeHtml(endDate)}.pdf` } };
