// Auth check
const input = $input.item.json;
const headers = input.headers || {};
const vapiSecret = headers['x-vapi-secret'] || '';
try {
  const expected = process.env.VAPI_WEBHOOK_SECRET || '';
  if (expected && vapiSecret !== expected) {
    return { _error: true, errorMessage: 'Pedido nao autorizado.', toolCallId: '' };
  }
} catch (e) {}

// Extract toolCallId first
let toolCallId = '';
try {
  toolCallId = input.body?.message?.toolCalls?.[0]?.id || '';
} catch (e) {}

try {
  // Extract body from Vapi format
  let body;
  if (input.body?.message?.toolCalls?.[0]?.function?.arguments) {
    body = input.body.message.toolCalls[0].function.arguments;
  } else if (input.body) {
    body = input.body;
  } else {
    body = input;
  }

  // Validate date
  if (!body.date) {
    return { _error: true, errorMessage: 'Por favor, indique a data para a qual pretende verificar a disponibilidade.', toolCallId };
  }

  // Parse date
  let dateStr;
  if (body.date.includes('T')) {
    dateStr = body.date.split('T')[0];
  } else {
    dateStr = body.date;
  }

  if (!/^\d{4}-\d{2}-\d{2}$/.test(dateStr)) {
    return { _error: true, errorMessage: 'Formato de data invalido. Por favor, indique a data novamente.', toolCallId };
  }

  // Validate date is real
  const [year, month, day] = dateStr.split('-').map(Number);
  const targetDate = new Date(year, month - 1, day);
  if (isNaN(targetDate.getTime()) || targetDate.getMonth() !== month - 1 || targetDate.getDate() !== day) {
    return { _error: true, errorMessage: 'Data invalida. Por favor, verifique o dia e mes.', toolCallId };
  }

  // Check past date
  const now = new Date();
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
  if (targetDate < today) {
    return { _error: true, errorMessage: 'Nao e possivel verificar disponibilidade para datas passadas. Por favor, escolha uma data futura.', toolCallId };
  }

  // Check weekend
  const dayOfWeek = targetDate.getDay();
  if (dayOfWeek === 0 || dayOfWeek === 6) {
    return { _error: true, errorMessage: 'Nao temos atendimento aos fins de semana. Por favor, escolha um dia util, de segunda a sexta-feira.', toolCallId };
  }

  return {
    _error: false,
    date: dateStr,
    timezone: 'Atlantic/Azores',
    service_type: body.service_type || '',
    toolCallId: toolCallId
  };
} catch (err) {
  return { _error: true, errorMessage: 'Ocorreu um erro ao verificar a disponibilidade. Por favor, tente novamente.', toolCallId };
}

// Define work hours (9h-18h)
const workStart = 9;
const workEnd = 18;
const slotDuration = 60; // minutes

// Get data from previous nodes
const extractData = $('Extract & Validate').item.json;
const targetDate = extractData.date;
const toolCallId = extractData.toolCallId;

// Check if calendar query failed
const allItems = $input.all();
if (allItems.length === 1 && allItems[0].json.error) {
  return {
    message: 'Desculpe, nao foi possivel verificar a disponibilidade neste momento. Por favor, tente novamente mais tarde.',
    toolCallId: toolCallId
  };
}

// Generate all possible slots
const allSlots = [];
for (let hour = workStart; hour < workEnd; hour++) {
  allSlots.push({
    time: String(hour).padStart(2, '0') + ':00',
    hour: hour,
    duration: slotDuration
  });
}

// Filter occupied slots
const availableSlots = allSlots.filter(slot => {
  const slotStart = new Date(targetDate + 'T' + slot.time + ':00');
  const slotEnd = new Date(slotStart.getTime() + slotDuration * 60000);

  return !allItems.some(event => {
    if (!event.json.start || !event.json.end) return false;
    const eventStart = new Date(event.json.start.dateTime || event.json.start.date);
    const eventEnd = new Date(event.json.end.dateTime || event.json.end.date);
    return (slotStart < eventEnd && slotEnd > eventStart);
  });
});

// Format voice-friendly response
let message;
if (availableSlots.length === 0) {
  message = 'Nao ha horarios disponiveis para esse dia. Por favor, escolha outra data.';
} else {
  // Format times for voice (e.g., '9 horas', '14 horas')
  const voiceTimes = availableSlots.map(s => s.hour + ' horas');

  if (availableSlots.length <= 3) {
    // Few slots: list them all
    message = 'Temos ' + availableSlots.length + (availableSlots.length === 1 ? ' horario disponivel' : ' horarios disponiveis') + ' para esse dia: ' + voiceTimes.join(', ') + '. Qual prefere?';
  } else {
    // Many slots: suggest first 3 and indicate more available
    const firstThree = voiceTimes.slice(0, 3).join(', ');
    message = 'Temos ' + availableSlots.length + ' horarios disponiveis para esse dia. As primeiras opcoes sao: ' + firstThree + '. Prefere um destes ou quer ouvir mais opcoes?';
  }
}

return {
  message: message,
  toolCallId: toolCallId,
  available: availableSlots.length > 0,
  total_slots: availableSlots.length,
  available_slots: availableSlots
};
