const body = $input.first().json.body || {};
const message = (typeof body.message === 'string') ? body.message : '';
let sessionId = (typeof body.session_id === 'string') ? body.session_id : '';
if (!sessionId || sessionId.trim() === '') {
  sessionId = 'SESSION-' + Date.now() + '-' + Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 8);
} else if (/\.\.[\/\\]/.test(sessionId)) {
  throw new Error('Invalid session_id format');
}
let sanitizedMessage = message.replace(/<[^>]*>/g, '').trim();
if (sanitizedMessage.length > 1500) {
  sanitizedMessage = sanitizedMessage.substring(0, 1500);
}
return {
  json: {
    message: '[Data atual: ' + (() => {
  const now = new Date();
  const month = now.getUTCMonth();
  const isDST = month >= 2 && month <= 9;
  const offset = isDST ? 0 : -1;
  const local = new Date(now.getTime() + offset * 3600000);
  const days = ['domingo','segunda-feira','terça-feira','quarta-feira','quinta-feira','sexta-feira','sábado'];
  const months = ['janeiro','fevereiro','março','abril','maio','junho','julho','agosto','setembro','outubro','novembro','dezembro'];
  return days[local.getUTCDay()] + ', ' + local.getUTCDate() + ' de ' + months[local.getUTCMonth()] + ' de ' + local.getUTCFullYear() + ', ' + String(local.getUTCHours()).padStart(2,'0') + ':' + String(local.getUTCMinutes()).padStart(2,'0');
})() + '] ' + (sanitizedMessage || 'hello'),
    session_id: sessionId,
    headers: $input.first().json.headers || {},
    query: $input.first().json.query || {}
  }
};

const inputData = $input.first().json;
const flowiseResponse = inputData.text || inputData.output || '';
const isFallback = !flowiseResponse || flowiseResponse.trim() === '';

let sessionId = 'unknown';
let empMsg = '';
try {
  const validateData = $('Validate Input').first().json;
  sessionId = validateData.session_id || 'unknown';
  empMsg = validateData.message || '';
} catch (e) {}

let output = flowiseResponse;
if (isFallback) {
  output = '[INTENT:error] Peço desculpa, mas não consegui processar o seu pedido neste momento. Por favor, tente novamente ou contacte o RH em rh@acortec.pt ou +351 295 300 200.';
}

// Extract [INTENT:xxx] tag
let llmIntent = null;
const intentMatch = output.match(/^\s*\[INTENT:(\w+)\]/);
if (intentMatch) {
  llmIntent = intentMatch[1].toLowerCase();
  output = output.replace(/^\s*\[INTENT:\w+\]\s*/, '');
}

// Check for VACATION_DATA
let vacationData = null;
let hasVacation = false;
const vacMatch = output.match(/\[VACATION_DATA\]\s*(\{[\s\S]*?\})\s*\[\/VACATION_DATA\]/);
if (vacMatch) {
  try {
    vacationData = JSON.parse(vacMatch[1].trim());
    if (!vacationData.employee_name || !vacationData.employee_id || !vacationData.department || !vacationData.start_date || !vacationData.end_date) {
      vacationData = null;
    } else {
      // Validate dates
      const startDate = new Date(vacationData.start_date);
      const endDate = new Date(vacationData.end_date);
      if (isNaN(startDate.getTime()) || isNaN(endDate.getTime()) || endDate <= startDate) {
        vacationData = null;
      } else {
        vacationData.reference_id = 'VAC-' + Date.now() + '-' + Math.random().toString(36).substring(2, 10).toUpperCase();
        vacationData.session_id = sessionId;
        vacationData.created_at = new Date().toISOString();
        for (const k of Object.keys(vacationData)) {
          if (typeof vacationData[k] === 'string') vacationData[k] = vacationData[k].replace(/[\n\r]/g, ' ').substring(0, 500);
        }
        hasVacation = true;
      }
    }
  } catch (e) { vacationData = null; }
}

// Check for ABSENCE_DATA
let absenceData = null;
let hasAbsence = false;
const absMatch = output.match(/\[ABSENCE_DATA\]\s*(\{[\s\S]*?\})\s*\[\/ABSENCE_DATA\]/);
if (absMatch) {
  try {
    absenceData = JSON.parse(absMatch[1].trim());
    if (!absenceData.employee_name || !absenceData.employee_id || !absenceData.department || !absenceData.date) {
      absenceData = null;
    } else {
      absenceData.reference_id = 'ABS-' + Date.now() + '-' + Math.random().toString(36).substring(2, 10).toUpperCase();
      absenceData.session_id = sessionId;
      absenceData.created_at = new Date().toISOString();
      for (const k of Object.keys(absenceData)) {
        if (typeof absenceData[k] === 'string') absenceData[k] = absenceData[k].replace(/[\n\r]/g, ' ').substring(0, 500);
      }
      hasAbsence = true;
    }
  } catch (e) { absenceData = null; }
}

// Check for REQUEST_DATA
let requestData = null;
let hasRequest = false;
const reqMatch = output.match(/\[REQUEST_DATA\]\s*(\{[\s\S]*?\})\s*\[\/REQUEST_DATA\]/);
if (reqMatch) {
  try {
    requestData = JSON.parse(reqMatch[1].trim());
    if (!requestData.employee_name || !requestData.employee_id || !requestData.department || !requestData.request_type || !requestData.description) {
      requestData = null;
    } else {
      requestData.reference_id = 'REQ-' + Date.now() + '-' + Math.random().toString(36).substring(2, 10).toUpperCase();
      requestData.session_id = sessionId;
      requestData.created_at = new Date().toISOString();
      for (const k of Object.keys(requestData)) {
        if (typeof requestData[k] === 'string') requestData[k] = requestData[k].replace(/[\n\r]/g, ' ').substring(0, 500);
      }
      hasRequest = true;
    }
  } catch (e) { requestData = null; }
}

let cleanResponse = output
  .replace(/\[VACATION_DATA\]\s*\{[\s\S]*?\}\s*\[\/VACATION_DATA\]/, '')
  .replace(/\[ABSENCE_DATA\]\s*\{[\s\S]*?\}\s*\[\/ABSENCE_DATA\]/, '')
  .replace(/\[REQUEST_DATA\]\s*\{[\s\S]*?\}\s*\[\/REQUEST_DATA\]/, '')
  .trim();

let classifiedIntent = 'general';
if (hasVacation) classifiedIntent = 'vacation_request';
else if (hasAbsence) classifiedIntent = 'absence_notification';
else if (hasRequest) classifiedIntent = 'general_request';
else if (llmIntent) classifiedIntent = llmIntent;

return [{
  json: {
    output: cleanResponse,
    hasVacation,
    hasAbsence,
    hasRequest,
    vacationData,
    absenceData,
    requestData,
    referenceId: hasVacation ? vacationData?.reference_id : (hasAbsence ? absenceData?.reference_id : (hasRequest ? requestData?.reference_id : null)),
    sessionId,
    originalMessage: empMsg,
    classifiedIntent,
    isFallback
  }
}];

let d;try{d=$('Process Response').first().json}catch(e){d={output:'Pedido processado.',sessionId:'unknown',referenceId:null}}
const ei=$input.first().json;const ok=ei&&!ei.error;
return[{json:{response:d.output,intent:'vacation_request',session_id:d.sessionId,reference_id:d.referenceId,vacation_submitted:ok,email_error:ok?undefined:(ei?.error?.message||'Failed'),timestamp:new Date().toISOString()}}];

