parameters.jsCode:
// Auth check
const input = $input.item.json;
const headers = input.headers || {};
const vapiSecret = headers['x-vapi-secret'] || '';
try {
  const expected = process.env.VAPI_WEBHOOK_SECRET || '';
  if (expected && vapiSecret !== expected) {
    return { _error: true, errorMessage: 'Pedido nao autorizado.', toolCallId: '' };
  }
} catch (e) {}

// Extract toolCallId first
let toolCallId = '';
try {
  toolCallId = input.body?.message?.toolCalls?.[0]?.id || '';
} catch (e) {}

try {
  // Extract body from Vapi format
  let body;
  if (input.body?.message?.toolCalls?.[0]?.function?.arguments) {
    body = input.body.message.toolCalls[0].function.arguments;
  } else if (input.body) {
    body = input.body;
  } else {
    body = input;
  }

  // Required fields
  const requiredFields = ['date', 'time', 'customer_name', 'customer_phone'];
  const missingFields = requiredFields.filter(field => !body[field]);
  if (missingFields.length > 0) {
    return { _error: true, errorMessage: 'Faltam dados obrigatorios para a marcacao. Por favor, forneca o nome, telefone, data e hora.', toolCallId };
  }

  // Validate date format
  if (!/^\d{4}-\d{2}-\d{2}$/.test(body.date)) {
    return { _error: true, errorMessage: 'Formato de data invalido. Por favor, indique a data novamente.', toolCallId };
  }

  // Validate time format
  if (!/^\d{2}:\d{2}$/.test(body.time)) {
    return { _error: true, errorMessage: 'Formato de hora invalido. Por favor, indique a hora novamente.', toolCallId };
  }

  // Parse date components
  const [year, month, day] = body.date.split('-').map(Number);
  const bookingDate = new Date(year, month - 1, day);
  if (isNaN(bookingDate.getTime()) || bookingDate.getMonth() !== month - 1 || bookingDate.getDate() !== day) {
    return { _error: true, errorMessage: 'Data invalida. Por favor, verifique o dia e mes.', toolCallId };
  }

  // Check past date
  const now = new Date();
  const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
  if (bookingDate < today) {
    return { _error: true, errorMessage: 'Nao e possivel marcar para uma data que ja passou. Por favor, escolha uma data futura.', toolCallId };
  }

  // Check weekend
  const dayOfWeek = bookingDate.getDay();
  if (dayOfWeek === 0 || dayOfWeek === 6) {
    return { _error: true, errorMessage: 'Nao temos atendimento aos fins de semana. Por favor, escolha um dia util, de segunda a sexta-feira.', toolCallId };
  }

  // Validate business hours (9:00 - 18:00)
  const [hour, minute] = body.time.split(':').map(Number);
  if (hour < 9 || hour >= 18) {
    return { _error: true, errorMessage: 'Esse horario esta fora do nosso periodo de atendimento. Estamos disponiveis das nove as dezoito horas, de segunda a sexta-feira.', toolCallId };
  }

  // Validate phone
  const phone = body.customer_phone.toString().replace(/\s+/g, '');
  if (phone.replace(/[^0-9]/g, '').length < 9) {
    return { _error: true, errorMessage: 'Numero de telefone invalido. Por favor, indique um numero valido.', toolCallId };
  }

  // Validate email if provided
  if (body.customer_email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(body.customer_email)) {
    return { _error: true, errorMessage: 'O endereco de email fornecido nao e valido.', toolCallId };
  }

  // Sanitize text inputs
  function sanitize(text) {
    if (!text) return '';
    return String(text).replace(/[\n\r]/g, ' ').replace(/<[^>]*>/g, '').substring(0, 500);
  }

  // Format date for natural voice
  const months = ['janeiro', 'fevereiro', 'marco', 'abril', 'maio', 'junho', 'julho', 'agosto', 'setembro', 'outubro', 'novembro', 'dezembro'];
  const voiceDate = day + ' de ' + months[month - 1] + ' de ' + year;

  // Format time for natural voice
  let voiceTime;
  if (minute === 0) {
    voiceTime = hour + ' horas';
  } else {
    voiceTime = hour + ' e ' + minute;
  }

  return {
    _error: false,
    date: body.date,
    time: body.time,
    voiceDate: voiceDate,
    voiceTime: voiceTime,
    customer_name: sanitize(body.customer_name),
    customer_phone: phone,
    customer_email: body.customer_email || '',
    service_type: sanitize(body.service_type) || 'Consulta',
    duration: body.duration || 60,
    notes: sanitize(body.notes) || '',
    toolCallId: toolCallId
  };
} catch (err) {
  return { _error: true, errorMessage: 'Ocorreu um erro ao processar o pedido de marcacao. Por favor, tente novamente.', toolCallId };
}

