parameters.jsCode:
const calendarResult = $input.item.json;
const bookingData = $('Extract & Validate Booking').item.json;

// Check if calendar creation succeeded
if (calendarResult.error || !calendarResult.id) {
  return {
    success: false,
    message: 'Desculpe, nao foi possivel confirmar a marcacao neste momento. Pode tentar novamente ou contactar-nos diretamente.',
    toolCallId: bookingData.toolCallId,
    customer_name: bookingData.customer_name,
    date: bookingData.date,
    time: bookingData.time,
    voiceDate: bookingData.voiceDate,
    voiceTime: bookingData.voiceTime,
    service_type: bookingData.service_type
  };
}

return {
  success: true,
  event_id: calendarResult.id,
  message: 'Perfeito! A sua marcacao esta confirmada para ' + bookingData.voiceDate + ' as ' + bookingData.voiceTime + '. Recebera uma confirmacao por email em breve.',
  toolCallId: bookingData.toolCallId,
  customer_name: bookingData.customer_name,
  customer_phone: bookingData.customer_phone,
  customer_email: bookingData.customer_email,
  date: bookingData.date,
  time: bookingData.time,
  voiceDate: bookingData.voiceDate,
  voiceTime: bookingData.voiceTime,
  service_type: bookingData.service_type,
  duration: bookingData.duration,
  notes: bookingData.notes
};
